INSERT INTO `mathonline`.`evaluation` (`id_evaluation`, `id_subtopic`, `question`, `correct_answer`, `wrong_answer_a`, `wrong_answer_b`, `wrong_answer_c`, `points`, `status`) VALUES (567, 17, 'La empresa donde yo trabajo tiene actualmente 375 empleados de tiempo completo. Se puso en práctica un plan de crecimiento para contratar 15 empleados nuevos cada 4 meses durante los próximos 5 años. Si la variable x representa el número de años transcurridas a partir del día de hoy y la variable y representa el número de empleados que la empresa tendrá si cumple con su plan de crecimiento, diga usted cuál ecuación describe la situación anterior:', '<math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mi mathvariant=\"normal\">y</mi><mo>=</mo><mn>45</mn><mi mathvariant=\"normal\">x</mi><mo>+</mo><mn>375</mn><mo>,</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mi mathvariant=\"normal\">x</mi><mo>=</mo><mn>0</mn><mo>,</mo><mo>.</mo><mo>.</mo><mo>.</mo><mo>,</mo><mn>5</mn></math>', '<math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mi mathvariant=\"normal\">y</mi><mo>=</mo><mn>15</mn><mi mathvariant=\"normal\">x</mi><mo>+</mo><mn>375</mn><mo>,</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mi mathvariant=\"normal\">x</mi><mo>=</mo><mn>0</mn><mo>,</mo><mo>.</mo><mo>.</mo><mo>.</mo><mo>,</mo><mn>5</mn></math>', '<math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mi mathvariant=\"normal\">x</mi><mo>=</mo><mn>15</mn><mi mathvariant=\"normal\">y</mi><mo>+</mo><mn>375</mn><mo>,</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mi mathvariant=\"normal\">y</mi><mo>=</mo><mn>0</mn><mo>,</mo><mo>.</mo><mo>.</mo><mo>.</mo><mo>,</mo><mn>5</mn></math>', '<math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mi mathvariant=\"normal\">x</mi><mo>=</mo><mn>45</mn><mi mathvariant=\"normal\">y</mi><mo>+</mo><mn>375</mn><mo>,</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mi mathvariant=\"normal\">y</mi><mo>=</mo><mn>0</mn><mo>,</mo><mo>.</mo><mo>.</mo><mo>.</mo><mo>,</mo><mn>5</mn></math>'