INSERT INTO `mathonline`.`evaluation` (`id_evaluation`, `id_subtopic`, `question`, `correct_answer`, `wrong_answer_a`, `wrong_answer_b`, `wrong_answer_c`, `points`, `status`) VALUES (568, 17, 'Tengo depositados $10,000 en el banco y pienso retirar $100 cada semana por las próximas 25 semanas.  Si la variable x representa el número de semanas transcurridas a partir del día de hoy y la variable y representa el saldo de mi cuenta en el banco, diga usted cuál ecuación describe la situación anterior:', '<math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mi mathvariant=\"normal\">y</mi><mo>=</mo><mo>-</mo><mn>100</mn><mi mathvariant=\"normal\">x</mi><mo>+</mo><mn>10000</mn><mo>,</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mi mathvariant=\"normal\">x</mi><mo>=</mo><mn>0</mn><mo>,</mo><mo>.</mo><mo>.</mo><mo>.</mo><mo>,</mo><mn>25</mn></math>', '<math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mi mathvariant=\"normal\">y</mi><mo>=</mo><mn>10000</mn><mi mathvariant=\"normal\">x</mi><mo>-</mo><mn>100</mn><mo>,</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mi mathvariant=\"normal\">x</mi><mo>=</mo><mn>0</mn><mo>,</mo><mo>.</mo><mo>.</mo><mo>.</mo><mo>,</mo><mn>25</mn></math>', '<math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mi mathvariant=\"normal\">y</mi><mo>=</mo><mo>-</mo><mn>100</mn><mi mathvariant=\"normal\">x</mi><mo>-</mo><mn>10000</mn><mo>,</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mi mathvariant=\"normal\">x</mi><mo>=</mo><mn>0</mn><mo>,</mo><mo>.</mo><mo>.</mo><mo>.</mo><mo>,</mo><mn>25</mn></math>', '<math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mi mathvariant=\"normal\">y</mi><mo>=</mo><mo>-</mo><mn>10000</mn><mi mathvariant=\"normal\">x</mi><mo>-</mo><mn>100</mn><mo>,</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mo>&#xA0;</mo><mi mathvariant=\"normal\">x</mi><mo>=</mo><mn>0</mn><mo>,</mo><mo>.</mo><mo>.</mo><mo>.</mo><mo>,</mo><mn>25</mn></math>', 5, 1);
