INSERT INTO `mathonline`.`evaluation` (`id_evaluation`, `id_subtopic`, `question`, `correct_answer`, `wrong_answer_a`, `wrong_answer_b`, `wrong_answer_c`, `points`, `status`) VALUES (582, 17, 'Tres familias desean perforar un pozo de tal manera que quede a la misma distancia de las 3 casas. Las casas se localizan cerca de un cruce de 2 carreteras perpendiculares. La familia A vive a 2 km. al Oeste y 5 km. al Norte de la intersección de las carreteras; la familia B vive a 2 km. al Este y 1 km. al Norte del mismo punto, y la familia C vive a 4 km. al Este y 5 km. al Norte. ¿Cuál debe ser la ubicación del pozo respecto a la intersección de las carreteras?', '1 km. al Este y 4 km. al Norte', '4 km. al Este y 2 km. al Norte', '2 km. al Este y 4 km. al Norte', '4 km. al Este y 1 km. al Norte', 5, 1);
